CREATE DATABASE `phonesystem`;     
CREATE TABLE `phonesystem`.`productos`( `codigo` VARCHAR(15) NOT NULL,`producto` VARCHAR(30) NOT NULL , `marca` VARCHAR(40) NOT NULL,`modelo` VARCHAR(40) NOT NULL,`estado` VARCHAR(15) NOT NULL,`minimo` INT(15) NOT NULL,`maximo` INT(15) NOT NULL,  PRIMARY KEY (`codigo`)) ENGINE = InnoDB;
CREATE TABLE `phonesystem`.`usuarios`( `nombre` VARCHAR(25) NOT NULL , `pass` VARCHAR(30) NOT NULL,`tipo` VARCHAR(15) NOT NULL,PRIMARY KEY (`nombre`)) ENGINE = InnoDB;
CREATE TABLE `phonesystem`.`proveedores`( `ìd` INT(50) NOT NULL,`nombre` VARCHAR(40) NOT NULL , `rif` VARCHAR(25) NOT NULL,`telefono` VARCHAR(20) NOT NULL,`ciudad` VARCHAR(25) NOT NULL,`email` VARCHAR(30) NOT NULL,`banco` VARCHAR(25) NOT NULL,`tipo de cuenta` VARCHAR(50) NOT NULL,`cuenta` VARCHAR(50) NOT NULL,`productos` VARCHAR(400) NOT NULL,`ingresado` VARCHAR(15) NOT NULL,PRIMARY KEY (`nombre`)) ENGINE = InnoDB;
INSERT INTO  `phonesystem`.`usuarios` (`nombre`, `pass`,`tipo`) VALUES ('admin', '12345', 'admin');
CREATE TABLE `phonesystem`.`solicitud de compra`( `id` INT(30) NOT NULL,`codigo` VARCHAR(15) NOT NULL,`producto` VARCHAR(40) NOT NULL,`marca` VARCHAR(40) NOT NULL,`modelo` VARCHAR(30) NOT NULL,`proveedor`  VARCHAR(30)  NOT NULL,`precio` FLOAT(15) NOT NULL,`cantidad` INT(40),`fecha` DATE NOT NULL,`factura` VARCHAR(15) NOT NULL,`entregado` VARCHAR(20) NOT NULL,PRIMARY KEY (`id`)) ENGINE = InnoDB;
CREATE TABLE `phonesystem`.`clientes telefonos`( `nombre` VARCHAR(40) NOT NULL , `ci` VARCHAR(15) NOT NULL,`telefono` VARCHAR(20) NOT NULL,`telefono opcional` VARCHAR(20) NOT NULL,`direccion` VARCHAR(100) NOT NULL,`email` VARCHAR(30) NOT NULL,`entrega` DATE NOT NULL,`nequipo` INT(40) NOT NULL,`marca` VARCHAR(40) NOT NULL,`modelo` VARCHAR(40) NOT NULL,`gama` VARCHAR(20) NOT NULL,`bateria` VARCHAR(20) NOT NULL,`memoria` VARCHAR(20) NOT NULL,`sim` VARCHAR(20) NOT NULL,`tapa` VARCHAR(20) NOT NULL,`diagnosticar` VARCHAR(20) NOT NULL, `fallas` VARCHAR(100) NOT NULL,`tecnico` VARCHAR(15) NOT NULL,`correciones` VARCHAR(100) NOT NULL,`imei` VARCHAR(40) NOT NULL ,`precio` FLOAT(15),`entregado` VARCHAR(15) NOT NULL,`fecha tope` DATE NOT NULL,`tipo de fallas`  VARCHAR(40) NOT NULL) ENGINE = InnoDB;
CREATE TABLE `phonesystem`.`fallas`(`nombre` VARCHAR(80) NOT NULL, `gama` VARCHAR(20) NOT NULL,`bateria` VARCHAR(20) NOT NULL,`memoria` VARCHAR(20) NOT NULL,`tapa` VARCHAR(20) NOT NULL,`fallas` VARCHAR(100) NOT NULL,`soluciones` VARCHAR(100) NOT NULL) ENGINE = InnoDB;
CREATE TABLE `phonesystem`.`inventario` ( `codigo` VARCHAR(15) NOT NULL , `producto` VARCHAR(40) NOT NULL ,`marca` VARCHAR(40) NOT NULL, `modelo` VARCHAR(40) NOT NULL , `estado` VARCHAR(15) NOT NULL ,`cantidad` VARCHAR(25) NOT NULL,`minimo` INT(15) NOT NULL, `maximo` INT(15) NOT NULL, `costo` VARCHAR(50) NOT NULL,`costo total` VARCHAR(50) NOT NULL, `precio` VARCHAR(50) NOT NULL,`precio total` VARCHAR(50) NOT NULL,PRIMARY KEY (`codigo`) ) ENGINE = InnoDB;
CREATE TABLE `phonesystem`.`chat` (`mensaje` VARCHAR(100)  NOT NULL, `emisor` VARCHAR(30) NOT NULL , `receptor` VARCHAR(30) NOT NULL,`hora` VARCHAR(30) NOT NULL) ENGINE = InnoDB;
CREATE TABLE `phonesystem`.`ventas` ( `codigo` VARCHAR(100) NOT NULL , `producto` VARCHAR(30) NOT NULL,`marca` VARCHAR(40) NOT NULL,`modelo` VARCHAR(40) NOT NULL,`cliente`  VARCHAR(50)  NOT NULL,`ci` VARCHAR(30) NOT NULL,`telefono` VARCHAR(20) NOT NULL,`email` VARCHAR(30) NOT NULL,`precio` FLOAT(15) NOT NULL,`cantidad` INT(30) NOT NULL,`fecha` DATE NOT NULL,`factura` VARCHAR(25) NOT NULL) ENGINE = InnoDB;
CREATE TABLE `phonesystem`.`compras` ( `codigo` VARCHAR(100) NOT NULL , `producto` VARCHAR(30) NOT NULL,`marca` VARCHAR(40) NOT NULL,`modelo` VARCHAR(40) NOT NULL,`proveedor`  VARCHAR(50)  NOT NULL,`precio` FLOAT(15) NOT NULL,`cantidad` INT(15) NOT NULL,`fecha` DATE NOT NULL,`factura` VARCHAR(15) NOT NULL) ENGINE = InnoDB;
CREATE TABLE `phonesystem`.`facturacion`( `email` VARCHAR(50) NOT NULL,`contraseña` VARCHAR(30) NOT NULL,`empresa` VARCHAR(50) NOT NULL,`habilitado` VARCHAR(20) NOT NULL) ENGINE = InnoDB;
INSERT INTO  `phonesystem`.`facturacion` (`email`, `contraseña`,`empresa`,`habilitado`) VALUES ('', '', 'PhoneSystem','Inhabilitado');
CREATE TABLE `phonesystem`.`pendientes`( `id` INT(130) NOT NULL,`email` VARCHAR(50) NOT NULL,`mensaje` VARCHAR(1000) NOT NULL) ENGINE = InnoDB;
CREATE TABLE `phonesystem`.`dispositivos`(`nmbre` VARCHAR(70) NOT NULL,`imagen` MEDIUMBLOB NOT NULL,  PRIMARY KEY (`nombre`)) ENGINE = InnoDB;
CREATE TABLE `phonesystem`.`factura`(`nmbre` VARCHAR(70) NOT NULL,`imagen` MEDIUMBLOB NOT NULL,  PRIMARY KEY (`nombre`)) ENGINE = InnoDB;
